import { useState } from "react";
import style from './Nav.module.css';
import {data, onSearch} from 'App'



function SearchBar({on}) {
   const [{character}, setCharacter] = useState('')
   
   const handleChange = (event) => { // evento que se dispara cuando hay cambios en el input (seteo del estado) y guarda el valor del input
      setCharacter({
         ...character,
         function() {
            data.push(event.target.value)
         }
      })
   };

   return (
      <div className={style.searchBar} >
         <input className={style.input} type='search' value={character} onChange={handleChange} /> {/* proceso para renderizar el guardado del input que cambia el estado */}
         <button className={style.button} onClick={() => onSearch(character)}>Agregar</button> {/* cuando se le pasa un parametro en el evento, se la ejecuta con una call back */}
      </div>
   );
}

export default SearchBar;
