import style from "./App.module.css";
import Cards from "./components/CARDS/Cards.jsx";
import Nav from "./components/NAV/Nav.jsx";
import {useState} from 'react';
import { Routes, Route, useParams } from "react-router-dom";
import About from "./components/ABOUT/About";
import Detail from './components/DETAIL/Detail'



function App() {

  const [characters, setCharacters] = useState([]);
  
  const params = useParams();

  const onSearch = (nombre) => {
    const URL_BASE = 'https://be-a-rym.up.railway.app/api';
    const API_KEY = '267df1b89911.cf3df61db637ff9b4e81';
    //
    fetch(`${URL_BASE}/character/${nombre}?key=${API_KEY}`)
      .then((response) => response.json())
      .then((data) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      })
  }

  const onClose = (id) => {
    setCharacters(
      characters.filter(nombre => nombre.id !== id)
    )
  }

  return (
    <div className={style.App} style={{ padding: "25px" }}>
        <Nav onSearch={onSearch}/>
        <Routes>
          <Route className={style.divBox} path="home" element={<Cards characters={characters} 
          onClose={onClose}/>}/>
          <Route path="about" element={<About/>}/>
          <Route path="/Detail/:detailId" element={<Detail/>}/>  {/* los ':' indican que es un params, es decir que lo que sigue es una variable */}
          {/* <Route path="/Nav" element={<Nav/>}>NAV</Route> */}
        </Routes>
        {/* <Route path="/" element={<Form/>}>LOGIN</Route> */}
    </div>
  );
}

export default App;
