import style from "./Card.module.css";
import { Link } from "react-router-dom";
import {agregarPersonaje, eliminarPersonaje} from '../../REDUX/actions';
import { connect } from "react-redux";
import { useState, useEffect} from "react";

const mapStateToProps = (state) => {
  return {
    myFavorites: () => {
      getSelection()
    }
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    agregarPersonaje: ()=> {
      dispatch(agregarPersonaje())
    },
    eliminarPersonaje: () => {
      dispatch(eliminarPersonaje())
    }
  }
}
  
const Card = ({ id, name, species, gender, image, onClose }) => {
  
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      mapDispatchToProps(eliminarPersonaje(id));
    } else {
      setIsFav(true);
      mapDispatchToProps(agregarPersonaje({ id, name, species, gender, image, onClose }))
    }
  }
  
  useEffect(() => {
    myFavorites.forEach((fav) => {
       if (fav.id === id) {
          setIsFav(true);
       }
    });
 }, [myFavorites]);

  return (
    <div className={style.card}>
      {
   isFav ? (
      <button onClick={handleFavorite}>‚ù§Ô∏è</button>
   ) : (
      <button onClick={handleFavorite}>ü§ç</button>
   )
}
      <button onClick={onClose} className={style.closeButton}>
        X
      </button>
      <img src={image} alt="Personaje elegido"/>
      <Link to={`/detail/${id}`}>
        <h2 className={style.name}>{name}</h2>
      </Link>
      <h2 className={style.gender} >Gender: {gender}</h2>
      <h2 className={style.species} >Species: {species}</h2> 
    </div>
    );
      
  }

export default connect (mapStateToProps, mapDispatchToProps) (Card);
