import { useState } from "react";
import onSearch from "App.js";

function SearchBar(props) {
   const [character, setCharacter] = useState('')
   
   const handleChange = (event) => { // evento que se dispara cuando hay cambios en el input (seteo del estado) y guarda el valor del input
      setCharacter(event.target.value)
   };

   return (
      <div className="searchBar" >
         <input className="input" type='search' value={character} onChange={handleChange} /> {/* proceso para renderizar el guardado del input que cambia el estado */}
         <button className="button" onClick={() => onSearch(character)}>Agregar</button> {/* cuando se le pasa un parametro en el evento, se la ejecuta con una call back */}
      </div>
   );
}

export default SearchBar;
