import style from './Detail.module.css'
import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";



const Detail = () => {
    
    let { detailId } = useParams(); // detailId es como el event.target.value pero de la URL es apra obtener el ID

    const [character, setCharacter] = useState({});

    useEffect(() => { // el useEffect simula los cilos de vida y me trae la información de un personaje en especifico y lo hace atravez de character
      const URL_BASE = 'https://be-a-rym.up.railway.app/api';
      const API_KEY = '267df1b89911.cf3df61db637ff9b4e81';
      //
        fetch(`${URL_BASE}/character/${detailId}?key=${API_KEY}`)
          .then((response) => response.json())
          .then((char) => {
            if (char.name) {
              setCharacter(char);
            } else {
              window.alert("No hay personajes con ese ID");
            }
          })
          .catch((err) => {
            window.alert("No hay personajes con ese ID");
          });
        return setCharacter({});
      }, [detailId]);
    
    return (
        <div>
            <h1>hola aqui van los detalles</h1>
            <h2>{character.name}</h2>
            <p>{character.status}</p>
            <p>{character.species}</p>
            <p>{character.gender}</p>
            <p>{character.origin}</p>

        </div>
    )
}

export default Detail;
